document.getElementById('reStart').style.display = 'none';
let scoreCard = document.getElementById("scoreCard");
let midArray = images.sort(() => Math.random() - 0.5);
let newArray = midArray.sort(() => Math.random() - 0.5);
//console.log(newArray)
let startButton = document.getElementById("start-button");
let nextButton = document.getElementById("next-image")
let card = document.getElementById("card")
let currentImage
let resultMessage = document.getElementById("result-message");  
let rightWwrong = document.getElementById("rightWrong");
let buttonsId = document.getElementById('buttonsId')
let image = document.getElementById('image')
let score = 0;
let keepScore = document.getElementById('keepScore')

let onePoint = document.getElementById('onePoint')
let twoPoints = document.getElementById('twoPoints')
let threePoints = document.getElementById('threePoints')
let left = document.getElementById('left')
let percent


onePoint.style.display = 'none'
twoPoints.style.display = 'none'
let bonus = 3;

buttonsId.style.display = 'none'
nextButton.style.display = 'none'
document.getElementById('image-container').style.display = 'none'

startButton.addEventListener("click", function() {
    document.getElementById('start').style.display = 'none'
    document.getElementById('image-container').style.display = 'flex'
    document.getElementById('next-image').style.display = 'block'
    document.getElementById('buttonsId').style.display = 'block'
    document.getElementById('start-button').style.display = 'none'
    nextButton.click();
});

threePoints.addEventListener("click", function() {
    threePoints.style.display = 'none'
    twoPoints.style.display = 'block'
    image.style.filter = `blur(3px)`;
    bonus = 2;
});

twoPoints.addEventListener("click", function() {
    twoPoints.style.display = 'none'
    onePoint.style.display = 'block'
    image.style.filter = `blur(0px)`;
    bonus = 1;
});


nextButton.addEventListener("click", next);
let counter = 0;

function next() {
    eh.style.display = 'none';
    card.style.display = 'none'
    bonus = 3;
    onePoint.style.display = 'none'
    twoPoints.style.display = 'none'
    threePoints.style.display = 'block'
    //keepScore.innerHTML = '007';
    image.style.filter = `blur(6px)`;
    rightWwrong.style.display = 'none'
    buttonsId.style.display = 'block'
    nextButton.style.display = 'none'
    image.src = newArray[counter];
    currentImage = (newArray[counter])
    counter = (counter + 1) % 16;
    if (counter === 0) {
    end.style.display = 'block'
    resultMessage.innerHTML = `<p>game over</p><p>You scored ${score} out of 45!</p>`
    document.getElementById('image-container').style.display = 'none'
    buttonsId.style.display = 'none'
    nextButton.style.display = 'none'
    document.getElementById('reStart').style.display = 'block';
    }
    //console.log(counter)
    percent = (counter/15)*100
    //console.log(percent)
    }


let buttons = document.querySelectorAll("#buttonsId button");
let i
buttons.forEach(button => {
    
    button.addEventListener('mouseover', function() {
      this.title = this.dataset.title;
      this.style.transform = 'scale(1.4)';
      })
     button.addEventListener('mouseout', function() {
    this.style.transform = 'scale(1)';
    });
      
    button.addEventListener("click", function() {
        eh.style.display = 'block';
        card.style.display = 'block'
        i = Math.floor(Math.random() * 4) +1;
        card.innerHTML = `<img class = 'small' src = 'images/${i}.jpg'>`
        //console.log(i)
        onePoint.style.display = 'none'
        twoPoints.style.display = 'none'
        threePoints.style.display = 'none'
        //console.log(this.id + " button was clicked!");
        image.style.filter = ('blur(0px')
        buttonsId.style.display = 'none'
        nextButton.style.display = 'block'
        rightWrong.style.display = 'block'
        //console.log(this.id)
        let pattern = new RegExp(`/${this.id}\\d`);
        if (pattern.test(currentImage)) {
            //console.log('ihurrah!');
            score+=bonus
            //console.log(score)
            
            rightWrong.innerHTML = `<span class="tick">&#10003;</span>` + this.getAttribute("data-title");
            rightWrong.style.color = 'black'
            keepScore.innerHTML = score
            
        } else {
            //console.log('not');
            rightWrong.innerHTML = `<span class="cross">&#10060;</span>` + this.getAttribute("data-title");
            rightWrong.style.color = 'red'
            
        }
    });
});

reStart.addEventListener("click", function(){
    window.location.reload()
})
  